INSERT INTO `destinasiwisata` (`id`, `judul`, `isi`, `foto`) VALUES
(1, 'Bendungan Sigura gura', '<div class=\"flex max-w-full flex-col flex-grow\"><div data-message-author-role=\"assistant\" data-message-id=\"0e44e71f-4354-48ec-a192-f6d2147fd715\" dir=\"auto\" class=\"min-h-8 text-message flex w-full flex-col items-end gap-2 whitespace-normal break-words [.text-message+&amp;]:mt-5\" data-message-model-slug=\"gpt-4o\"><div class=\"flex w-full flex-col gap-1 empty:hidden first:pt-[3px]\"><div class=\"markdown prose w-full break-words dark:prose-invert dark\"><p style=\"text-align: justify; \"><span style=\"font-family: &quot;Segoe UI&quot;;\"><b>Bendungan Sigura-gura adalah bendungan terbesar kedua di Indonesia</b> setelah Bendungan Sutami, terletak 23,3 km dari hulu Sungai Asahan (Danau Toba), atau 8,8 km dari Bendungan Siruar, dan hanya 1 km di hilir Air Terjun Sigura-gura. Bendungan ini memiliki peran penting dalam menjamin ketersediaan air untuk Pembangkit Listrik Tenaga Air (PLTA) Sigura-gura.&nbsp;</span><span style=\"font-family: &quot;Segoe UI&quot;;\">Pembangunan bendungan ini dimulai pada Mei 1978 dan selesai pada Desember 1981. Bendungan Sigura-gura dibangun dari beton dengan tinggi 46 meter dari dasar Sungai Asahan dan volume tubuh bendungan mencapai 38.000 m³. Bendungan ini dirancang untuk memenuhi kebutuhan listrik dari pabrik aluminium milik Inalum yang terletak di Kuala Tanjung.</span></p><p style=\"text-align: justify;\"><br></p></div></div></div></div><div class=\"mb-2 flex gap-3 empty:hidden -ml-2\"><div class=\"items-center justify-start rounded-xl p-1 flex\"><div class=\"flex items-center\"><span class=\"\" data-state=\"closed\"><button class=\"rounded-lg text-token-text-secondary hover:bg-token-main-surface-secondary\" aria-label=\"Read aloud\" data-testid=\"voice-play-turn-action-button\" fdprocessedid=\"j443hg\"><span class=\"flex h-[30px] w-[30px] items-center justify-center\"><svg width=\"24\" height=\"24\" viewBox=\"0 0 24 24\" fill=\"none\" xmlns=\"http://www.w3.org/2000/svg\" class=\"icon-md-heavy\"><path fill-rule=\"evenodd\" clip-rule=\"evenodd\" d=\"M11 4.9099C11 4.47485 10.4828 4.24734 10.1621 4.54132L6.67572 7.7372C6.49129 7.90626 6.25019 8.00005 6 8.00005H4C3.44772 8.00005 3 8.44776 3 9.00005V15C3 15.5523 3.44772 16 4 16H6C6.25019 16 6.49129 16.0938 6.67572 16.2629L10.1621 19.4588C10.4828 19.7527 11 19.5252 11 19.0902V4.9099ZM8.81069 3.06701C10.4142 1.59714 13 2.73463 13 4.9099V19.0902C13 21.2655 10.4142 22.403 8.81069 20.9331L5.61102 18H4C2.34315 18 1 16.6569 1 15V9.00005C1 7.34319 2.34315 6.00005 4 6.00005H5.61102L8.81069 3.06701ZM20.3166 6.35665C20.8019 6.09313 21.409 6.27296 21.6725 6.75833C22.5191 8.3176 22.9996 10.1042 22.9996 12.0001C22.9996 13.8507 22.5418 15.5974 21.7323 17.1302C21.4744 17.6185 20.8695 17.8054 20.3811 17.5475C19.8927 17.2896 19.7059 16.6846 19.9638 16.1962C20.6249 14.9444 20.9996 13.5175 20.9996 12.0001C20.9996 10.4458 20.6064 8.98627 19.9149 7.71262C19.6514 7.22726 19.8312 6.62017 20.3166 6.35665ZM15.7994 7.90049C16.241 7.5688 16.8679 7.65789 17.1995 8.09947C18.0156 9.18593 18.4996 10.5379 18.4996 12.0001C18.4996 13.3127 18.1094 14.5372 17.4385 15.5604C17.1357 16.0222 16.5158 16.1511 16.0539 15.8483C15.5921 15.5455 15.4632 14.9255 15.766 14.4637C16.2298 13.7564 16.4996 12.9113 16.4996 12.0001C16.4996 10.9859 16.1653 10.0526 15.6004 9.30063C15.2687 8.85905 15.3578 8.23218 15.7994 7.90049Z\" fill=\"currentColor\"></path></svg></span></button></span><span class=\"\" data-state=\"closed\"><button class=\"rounded-lg text-token-text-secondary hover:bg-token-main-surface-secondary\" aria-label=\"Salin\" data-testid=\"copy-turn-action-button\" fdprocessedid=\"392y6\"><span class=\"flex h-[30px] w-[30px] items-center justify-center\"><svg width=\"24\" height=\"24\" viewBox=\"0 0 24 24\" fill=\"none\" xmlns=\"http://www.w3.org/2000/svg\" class=\"icon-md-heavy\"><path fill-rule=\"evenodd\" clip-rule=\"evenodd\" d=\"M7 5C7 3.34315 8.34315 2 10 2H19C20.6569 2 22 3.34315 22 5V14C22 15.6569 20.6569 17 19 17H17V19C17 20.6569 15.6569 22 14 22H5C3.34315 22 2 20.6569 2 19V10C2 8.34315 3.34315 7 5 7H7V5ZM9 7H14C15.6569 7 17 8.34315 17 10V15H19C19.5523 15 20 14.5523 20 14V5C20 4.44772 19.5523 4 19 4H10C9.44772 4 9 4.44772 9 5V7ZM5 9C4.44772 9 4 9.44772 4 10V19C4 19.5523 4.44772 20 5 20H14C14.5523 20 15 19.5523 15 19V10C15 9.44772 14.5523 9 14 9H5Z\" fill=\"currentColor\"></path></svg></span></button></span><div class=\"flex\"><span class=\"\" data-state=\"closed\"><button class=\"rounded-lg text-token-text-secondary hover:bg-token-main-surface-secondary\" aria-label=\"Respons yang bagus\" data-testid=\"good-response-turn-action-button\" fdprocessedid=\"yas7oo\"><span class=\"flex h-[30px] w-[30px] items-center justify-center\"><svg width=\"24\" height=\"24\" viewBox=\"0 0 24 24\" fill=\"none\" xmlns=\"http://www.w3.org/2000/svg\" class=\"icon-md-heavy\"></svg></span></button></span></div></div></div></div>', '671ee801de8016-229d8531c2d3a372961b2325cbac1496-55ec5e8fdc47b69b21a50a8c355fda95.jpg'),
(7, 'Air Terjun Ponot', '<p style=\"text-align: justify; \"><b><span style=\"font-family: &quot;Segoe UI&quot;;\">Air Terjun tertinggi di Indonesia berada di Parhitean Asahan.&nbsp;</span></b><span lang=\"EN-ID\" style=\"font-family: &quot;Segoe UI&quot;;\">Air Terjun Ponot adalah sebuah air terjun\r\nkebanggaan masyarakat Sumatera Utara, dengan sajian keindahan dari sepanjang\r\njalan hingga sampai di lokasi utama.</span><span lang=\"EN-ID\" style=\"font-family: &quot;Segoe UI&quot;;\"> </span><span lang=\"SV\" style=\"font-family: &quot;Segoe UI&quot;;\">Tahun 2019, beberapa kota di Pulau Jawa\r\nmengalami Blackout secara bersamaan. Kejadian tersebut menjadi sebuah kesadaran\r\nbetapa pentingnya listrik dalam kehidupan manusia. Sumber listrik salah-satunya\r\nbisa berasal dari uap, dan air. Dan tahukah anda bahwa salah satu air terjun\r\ntertinggi di Indonesia ini penyumbang bagi PLTA Sigura-gura?</span></p><p style=\"text-align: justify; \"><span style=\"font-family: &quot;Segoe UI&quot;;\">Lokasi dan alamat Air Terjun Ponot berada di Jl.\r\nSigura-Gura, Desa Tangga, Kecamatan Aek Songsongan, Kabupaten Asahan, Provinsi\r\nSumatera Utara.&nbsp;</span></p>', '671f38a1d52e8Daya-Tarik-Air-Terjun-Ponot.webp');

